mapper.select:
SELECT cjc.*, tsm.name merchantName, tsd.name deptName FROM cms_job_category cjc LEFT JOIN t_sys_dept tsd ON tsd.id = cjc.dept_id LEFT JOIN t_sys_merchant tsm ON tsm.id = cjc.merchant_id
cjc.is_deleted=0
AND cjc.merchant_id = #{filter.merchantId}
AND cjc.dept_id = #{filter.deptId}
AND cjc.name LIKE CONCAT( '%', #{filter.name} , '%' )
ORDER BY cjc.sort ASC, cjc.create_time DESC

SELECT id, name FROM cms_job_category
is_deleted=0 AND status=1
AND merchant_id = #{filter.merchantId}
AND dept_id = #{filter.deptId}
AND name LIKE CONCAT( '%', #{filter.name} , '%' )
ORDER BY sort ASC, create_time DESC
