mapper.select:
SELECT cjt.*, tsd.name deptName FROM cms_job_tag cjt LEFT JOIN t_sys_dept tsd ON tsd.id = cjt.dept_id
cjt.is_deleted=0
AND cjt.merchant_id = #{filter.merchantId}
AND cjt.dept_id = #{filter.deptId}
AND cjt.name LIKE CONCAT( '%', #{filter.name} , '%' )
ORDER BY cjt.sort ASC, cjt.create_time DESC

SELECT id, name FROM cms_job_tag
is_deleted=0 AND status=1
AND merchant_id = #{filter.merchantId}
AND dept_id = #{filter.deptId}
AND name LIKE CONCAT( '%', #{filter.name} , '%' )
ORDER BY sort ASC, create_time DESC
