mapper.select:
SELECT jcpr.id, jcpr.category_id, jcpr.sort, jcpr.is_show, jcpr.is_put, jcpr.category_id, jcpr.spu_id, jp.name spuName FROM pms_category_spu_relation jcpr JOIN pms_spu jp ON jcpr.spu_id = jp.id WHERE jcpr.is_deleted = 0
AND jcpr.category_id = #{filter.categoryId}
ORDER BY jcpr.sort DESC, jcpr.create_time DESC

SELECT jcpr.id, jcpr.category_id , jcpr.spu_id FROM pms_category_spu_relation jcpr WHERE jcpr.is_deleted = 0
AND jcpr.category_id = #{filter.categoryId}
ORDER BY jcpr.sort DESC

mapper.update:
UPDATE pms_category_spu_relation
SET is_show = #{isShow}
WHERE is_deleted = 0 AND id IN

    #{id}

UPDATE pms_category_spu_relation
SET is_put = #{isPut}
WHERE is_deleted = 0 AND id IN

    #{id}

UPDATE pms_category_spu_relation
SET is_show = #{isShow}
WHERE is_deleted = 0 AND spu_id IN

    #{spuId}

UPDATE pms_category_spu_relation
SET is_put = #{isPut}
WHERE is_deleted = 0 AND spu_id IN

    #{spuId}

update.foreach:
UPDATE pms_category_spu_relation
SET sort = #{item.sort}
WHERE id = #{item.id}
