mapper.insert:
INSERT t_sys_property ( id, `key`, value, remark, create_by, update_by, create_time, update_time ) VALUES
( #{item.id}, #{item.key}, #{item.value}, #{item.remark}, #{item.currentUserId}, #{item.currentUserId}, now(), now() )
ON DUPLICATE KEY UPDATE `key` = VALUES(`key`), value = VALUES(value), remark = VALUES(remark), update_by = VALUES(update_by), update_time = VALUES(update_time);
